# Clases
class conjunto:
    letra = str()
    def __init__(self, letra = str()):
        self.conjunto = []
        self.letra = letra
        
    @property
    def Letra(self):
        return self.letra

    @Letra.setter
    def Letra(self, letra):
        self.letra = letra

# Variables

